DECLARE
    @constraintName sysname
  , @query nvarchar(4000)

SELECT @constraintName = (
    SELECT object_name(CDEFAULT)
    FROM syscolumns
    WHERE ID = object_id('wf_workflow_trace')
        AND NAME = 'created_ts'
)

SET @query = '
    ALTER TABLE wf_workflow_trace
    DROP ' + @constraintName + '
'

EXEC sys.sp_executesql @query;

